$(function(){
	$('.search-box').click(function(){
		if ($('.down').is(':hidden')) {
			$('.down').show();
			$('.down li:first').css('backgroundColor','#232323');
		}
		else {	
			$('.down').hide();
		}
	})


	$('.colorall li   span,img').click(function(){
		if ($(this).parents("li").find(".show-detail2").is(':hidden')) {
			$(this).parents("li").find(".show-detail2,.show-detail3").fadeIn()
			$(this).parents("li").find(".colorall-text").fadeOut()
			$(this).parents("li").css({'box-shadow':'18px 3px 8px rgba(0,0,0,.2)','backgroundColor':'#fff'})
		}
		else{
			$(this).parents("li").find(".show-detail2,.show-detail3").fadeOut()
			$(this).parents("li").find(".colorall-text").fadeIn()
			$(this).parents("li").css({'backgroundColor':'rgba(0,0,0,.0)','box-shadow':'none'})
		}
		
	})

	$('.down li').click(function(){
		if ($(this).find('.line').is(':hidden')) {
			$(this).find('.line').css({'display':'block'})
			$(this).css({'backgroundColor':'#232323'})
			$(this).siblings('li').css({'backgroundColor':'#3d3d3d'})
			$(this).siblings('li').find('.line').css({'display':'none'})
		}
		else {
			// 高光

		}
	})


	
	
	var  chance = false;
	var  chance2 = false;
	var  chance3 = false;
	jQuery(window).scroll( function (){
		
        var  topScroll = jQuery(window).scrollTop();
        if ( topScroll >= 400 && chance != true) {
			$('.red').find('.show-detail2 , .show-detail3').fadeIn(2000);
			$('.red').find('.colorall-text').fadeOut();
			$('.red').css({'box-shadow':'18px 3px 8px rgba(0,0,0,.2)','backgroundColor':'#fff','transition-delay':'0.2s'})
			chance = true;
		}
		 if ( topScroll >= 500 && chance2 != true)
		{
			$('.blue').find('.show-detail2 , .show-detail3').fadeIn(2000);
			$('.blue').find('.colorall-text').fadeOut();
			$('.blue').css({'box-shadow':'18px 3px 8px rgba(0,0,0,.2)','backgroundColor':'#fff','transition-delay':'0.2s'})
			chance2 = true;
		}
		if ( topScroll >= 600 && chance3 != true ) 
		{
			$('.green').find('.show-detail2 , .show-detail3').fadeIn(2000);
			$('.green').find('.colorall-text').fadeOut();
			$('.green').css({'box-shadow':'18px 3px 8px rgba(0,0,0,.2)','backgroundColor':'#fff','transition-delay':'0.2s'})
			chance3 = true;
		}
    });





	
		
	
	
})


