$(function(){
	$('.search-box').click(function(){
		if ($('.hclab input').is(':hidden')) {
			$('.hclab input').hide();
			$('.hclab img').show();
			$('.down').hide();

		}
		else {
			$('.hclab input').show();
			$('.down').show();
			$('.down li:last').css('backgroundColor','#232323');
			
			
		}
	})



	$('.down li').click(function(){
		if ($(this).find('.line').is(':hidden')) {
			$(this).find('.line').css({'display':'block'})
			$(this).css({'backgroundColor':'#232323'})
			$(this).siblings('li').css({'backgroundColor':'#3d3d3d'})
			$(this).siblings('li').find('.line').css({'display':'none'})
			$('.down i').css({'display':'none'})
		}
		else {
			// 高光
			
		}
	})


	






	
		
	
	
})
